import React from "react";
import StripeCheckout from "react-stripe-checkout";
import axios from "axios";

import { connect } from "react-redux";
import { clearAllItems } from "../../redux/cart/cart-actions";

const StripeCheckoutButton = ({ price, clearAllItems }) => {
  const priceForStripe = price * 100;
  const publishableKey = "pk_test_WBqax2FWVzS9QlpJScO07iuL";
  console.log(price)

  const onToken = async token => {
    // clearAllItems();
    try {
      await axios({
        url: "payment",
        method: "post",
        data: {
          amount: priceForStripe,
          token
        }
      });

      alert("Payment successful");
    } catch (error) {
      alert(
        "There was an issue with your payment. Please use the provided credit card"
      );
      console.log("Payment error: ", error);
    }
  };

  return (
    <StripeCheckout
      label="Pay Now"
      name="CRWN Clothing Ltd."
      billingAddress
      shippingAddress
      image="https://svgshare.com/i/CUz.svg"
      description={`Your total is $${price}`}
      amount={priceForStripe}
      panelLabel="Pay Now"
      token={onToken}
      stripeKey={publishableKey}
    />
  );
};

const mapDispatchToProps = dispatch => ({
  clearAllItems: () => dispatch(clearAllItems())
});

export default connect(
  null,
  mapDispatchToProps
)(StripeCheckoutButton);
